import React, { useState } from 'react';
import styled from 'styled-components';

const App = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [isActive, setIsActive] = useState(false);

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
    setIsActive(event.target.value.length > 0);
  };

  return (
    <Container>
      <SearchBarContainer active={isActive}>
        <SearchInput
          type="text"
          placeholder="Search here..."
          value={searchTerm}
          onChange={handleInputChange}
        />
      </SearchBarContainer>
      {isActive && (
        <SearchResults>
          <p>Results for: <strong>{searchTerm}</strong></p>
          {/* Mock Search Results */}
          <ul>
            <li>Result 1 for {searchTerm}</li>
            <li>Result 2 for {searchTerm}</li>
            <li>Result 3 for {searchTerm}</li>
          </ul>
        </SearchResults>
      )}
    </Container>
  );
};

export default App;

// Styled components
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #f5f5f5;
`;

const SearchBarContainer = styled.div`
  position: absolute;
  top: ${({ active }) => (active ? '20%' : '50%')};
  transform: translateY(-50%);
  transition: top 0.4s ease;
  width: 50%;
`;

const SearchInput = styled.input`
  width: 100%;
  padding: 10px 20px;
  font-size: 1.2rem;
  border-radius: 25px;
  border: 1px solid #ccc;
  box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;

  &:focus {
    border-color: #007bff;
    box-shadow: 0px 4px 6px rgba(0, 123, 255, 0.5);
    outline: none;
  }
`;

const SearchResults = styled.div`
  margin-top: 60px;
  width: 50%;
  background: #fff;
  box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
  padding: 20px;
  border-radius: 10px;

  ul {
    list-style-type: none;
    padding: 0;

    li {
      padding: 10px 0;
      border-bottom: 1px solid #ddd;

      &:last-child {
        border-bottom: none;
      }
    }
  }
`;
