import React, { useState } from 'react';
import styled, { keyframes } from 'styled-components';

const App = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
    setError(''); // Clear error when typing
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter' && searchTerm.trim() !== '') {
      handleSearch();
    }
  };

  const handleSearch = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://127.0.0.1:5000/api/search', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ query: searchTerm }),
      });

      if (!response.ok) {
        throw new Error(`Error: ${response.status}`);
      }

      const data = await response.json();
      setSearchResults(data.results || []);
    } catch (error) {
      console.error('Error fetching data:', error);
      setSearchResults([]); // Ensure previous results are cleared
      setError('An error occurred while fetching results.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleClear = () => {
    setSearchTerm('');
    setSearchResults([]);
    setError('');
  };

  return (
    <Container>
      <Heading>QueryMind</Heading>
      <SearchBarContainer>
        <SearchInput
          type="text"
          placeholder="Enter your query here..."
          value={searchTerm}
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
        />
        <SearchButton onClick={handleSearch}>Search</SearchButton>
        <ClearButton onClick={handleClear}>Clear</ClearButton>
      </SearchBarContainer>
      {error && <ErrorText>{error}</ErrorText>}
      {isLoading && <LoadingText>Loading...</LoadingText>}
      {searchResults.length > 0 && (
        <ResultsContainer>
          {searchResults.map((result, index) => (
            <ResultBlock key={index}>
              {typeof result === 'string' ? (
                <p>{result}</p>
              ) : (
                <>
                  <h3>{result.title || result.title}</h3>
                  <p>{result.abstract || JSON.stringify(result)}</p>
                </>
              )}
            </ResultBlock>
          ))}
        </ResultsContainer>
      )}
    </Container>
  );
};

export default App;

// Styled Components
const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  height: 100vh;
  background-image: url('/Users/vsmourya/Desktop/MS/Sem_courses/sem3/560_BigData_Analysis/QueryMind/bg.png');
  background-size: cover;
  background-position: center;
  color: #5C7C89;
  padding-top: 20px;
  overflow: hidden;
`;

const Heading = styled.h1`
  font-size: 3rem;
  color: #FFFFFF;
  margin-bottom: 0px;
  font-family: 'Product Sans', sans-serif;
  position: sticky;
  top: 0;
  background-color: rgba(1, 20, 37, 0.8); /* Semi-transparent background */
  width: 100%;
  text-align: center;
  z-index: 100;
`;

const SearchBarContainer = styled.div`
  margin-top: 20px;
  width: 60%;
  display: flex;
  align-items: center;
  gap: 10px;
`;

const SearchInput = styled.input`
  flex: 1;
  padding: 10px 20px;
  font-size: 1.2rem;
  border-radius: 25px;
  border: 1px solid #5C7C89;
  background-color: #242424;
  color: #FFFFFF;
  box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.2);
  transition: all 0.3s ease;

  &:focus {
    border-color: #1F4959;
    box-shadow: 0px 4px 6px rgba(31, 73, 89, 0.5);
    outline: none;
  }
`;

const SearchButton = styled.button`
  padding: 10px 20px;
  font-size: 1.2rem;
  border-radius: 25px;
  background-color: #1F4959;
  color: #FFFFFF;
  border: none;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #011425;
  }
`;

const ClearButton = styled(SearchButton)`
  background-color: #5C7C89;
  &:hover {
    background-color: #1F4959;
  }
`;

const ResultsContainer = styled.div`
  margin-top: 20px;
  width: 60%;
  max-height: 70vh;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
  gap: 15px;
  animation: ${fadeIn} 0.6s ease-in-out;
  background-color: rgba(0, 0, 0, 0.6); /* Transparent dark background */
  border-radius: 15px;
  padding: 10px;
`;

const ResultBlock = styled.div`
  background: #242424;
  border-radius: 10px;
  box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.2);
  padding: 20px;
  animation: ${fadeIn} 0.5s ease-in-out;

  h3 {
    font-size: 1.5rem;
    margin-bottom: 10px;
    color: #FFFFFF;
  }

  p {
    font-size: 1rem;
    color: #5C7C89;
  }
`;

const ErrorText = styled.p`
  margin-top: 10px;
  color: #FF5C5C;
  font-size: 1rem;
`;

const LoadingText = styled.p`
  margin-top: 10px;
  font-size: 1rem;
  color: #FFFFFF;
`;
